const camelToKebab = (key: string): string =>
  key.replace(/([a-z0-9])([A-Z])/g, "$1-$2").toLowerCase();

const useCSS = (styles: CSSProperties): string =>
  Object.entries(styles)
    .filter(([_, value]) => value !== undefined && value !== null)
    .map(([key, value]) => `${camelToKebab(key)}:${value};`)
    .join("");

const useCssWithStyleAttr = (styles: CSSProperties): string =>
  `style="${useCSS(styles)}"`;

const centerIcon = (
  maxWidth: number,
  adjustTop: number = 0,
  adjustLeft: number = 0
) => {
  const topLeft = (100 - maxWidth) / 2;
  const withPercent = (val: number) => val + "%";
  return {
    position: "relative",
    maxWidth: withPercent(maxWidth),
    top: withPercent(topLeft + adjustTop),
    left: withPercent(topLeft + adjustLeft),
  } as CSSProperties;
};

